@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;700;900&display=swap');

/* ============================================ 
                  global vars
============================================  */
:root {
  /* base colors */
  --clr-base: #4f596c;
  --clr-base-dk: #232b37;
  --clr-base-lt: #f4f9fc;

  /* primary colors */
  --clr-primary: #0980fa;
  --clr-primary-lt: #dcecf4;

  /* colors */
  --clr-secondary: #ff9f3d;
  --clr-third: #ffdd0c;

  /* font family */
  --ff-base: 'Inter', sans-serif;

  /*  font sizes */
  --fs-300: 0.9rem;
  --fs-320: 1rem;
  --fs-350: 1.2rem;
  --fs-400: 1.3rem;
  --fs-450: 1.5rem;
  --fs-500: 1.8rem;
  --fs-600: 2.4rem;
  --fs-650: 2.8rem;
  --fs-700: 3.1rem;

  /* mobile screen */
  --fs-base: var(--fs-300);
  --fs-hero-heading: var(--fs-600);
  --fs-heading: var(--fs-500);
  --fs-sub-heading: var(--fs-400);
  --fs-sub-title: var(--fs-300);
  --fs-btn: var(--fs-300);

  /* font weight */
  --fw-normal: 500;
  /* --fw-medium: 500; */
  --fw-semibold: 600;
  --fw-bold: 700;
  --fw-extraBold: 900;

  --screen-Max-Width: 1220px;
  --sections-spacing: 6rem;
  --sections-padding: 1.2rem;

  /* 2 / 4 / 8 / 12 / 16 / 24 / 32 / 48 / 64 / 80 / 96 / 128
*/
  --spacing-s: 8px;
  --spacing-m: 12px;
  --spacing-l: 16px;
  --spacing-l1: 24px;
  --spacing-l2: 32px;
  --spacing-l3: 48px;
  --spacing-l4: 64px;
  --spacing-l5: 96px;
  --spacing-l6: 128px;

  /* border raduse */
  --border-r: 0.3125rem;

  --text-max-width: min(80%, 30em);

  /* --shadow */
  --shadow: 0px 4px 9.9px 0px rgba(120, 129, 144, 0.15);
  --shadow-lt: 0px 0px 13px -6px rgba(0, 0, 0, 0.51);

  /* transitions */
  --base-transition: all 280ms ease;

  --phone-breakpoint: 50em;
  --tap-breakpoint: 70em;
}

/* global classes */
.container {
  padding: var(--sections-padding);
  width: min(var(--screen-Max-Width), 100%);
  margin: 0 auto;
}

@media (min-width: 50em) {
  :root {
    --fs-base: var(--fs-400);
    --fs-hero-heading: var(--fs-700);
    --fs-heading: var(--fs-600);
    --fs-sub-title: var(--fs-400);
    --fs-btn: var(--fs-400);
  }
}

/* ============================================ 
                  global css
============================================  */
/* html {
  to make every 1rem = 10px 
   10px / 16px = 0.625 = 62.5% 
  font-size: 62.5%;
} */
/* @media (min-width: var(--phone-breakpoint)) {
  html {
    font-size: 70%;

} */

body {
  /* typography */
  color: var(--clr-base);
  font-family: 'Inter', sans-serif;
  font-size: var(--fs-base);
  font-weight: var(--fw-normal);
  /* letter-spacing: 0.6px; */
  line-height: 1.2;
  text-rendering: optimizeSpeed;

  /* body style */
  background-color: var(--clr-base-lt);
  min-height: 100vh;
}

svg {
  width: 2rem;
  height: 2rem;
  fill: var(--clr-base);
}

/* ============================================ 
                  global classes
============================================  */
.bk-clr-primarylt {
  background-color: var(--clr-primary-lt);
}
.flex-row {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
}
.flex-column {
  display: flex;
  flex-direction: column;
  align-items: baseline;
}
.clr-base {
  background-color: var(--clr-base);
}

.even-columns {
  display: grid;
  gap: 2rem;
}
.even-columns > * {
  justify-self: start;
  align-self: center;
  overflow: hidden;
}

@media (min-width: 50em) {
  .even-columns {
    grid-auto-flow: column;
    grid-auto-columns: 1fr;
  }
}

.btn {
  font-weight: var(--fw-semiBold);
  font-size: var(--fs-btn);
  color: var(--clr-base-lt);
  background-color: var(--clr-primary);
  border-radius: var(--border-r);
  display: inline-block;
  cursor: pointer;
  padding: var(--spacing-s) var(--spacing-l);
  margin: 1rem 0;
  border: none;
  transition: var(--base-transition);
}
.btn:hover {
  transform: translateY(-1.5px);
}
/* for sections with smooth reveal */
.hid-section {
  transform: translateY(50%);
  filter: blur(5px);
  opacity: 0;
}

.link-hover:hover {
  color: var(--clr-primary);
  transition: color ease-in-out 200ms;
}

.icon {
  width: 32px;
  height: 32px;
}
.icon-24px {
  width: 24px;
  height: 24px;
}
.overlay-blur {
  position: absolute;
  top: 0;
  right: 0;
  width: 100%;
  height: 100%;
  z-index: 10;
  backdrop-filter: blur(5px);
}

.hidden {
  display: none;
}
.alert {
  position: fixed;
  top: 0;
  left: 50%;
  -webkit-transform: translateX(-50%);
  transform: translateX(-50%);
  z-index: 9999;
  color: var(--clr-base-dk);
  font-size: 1.8rem;
  font-weight: 400;
  text-align: center;
  border-bottom-left-radius: 5px;
  border-bottom-right-radius: 5px;
  padding: 1rem 5rem;
  -webkit-box-shadow: 0 2rem 4rem rgba(0, 0, 0, 0.25);
  box-shadow: 0 2rem 4rem rgba(0, 0, 0, 0.25);
}
.alert--success {
  background-color: var(--clr-primary);
}
.alert--error {
  background-color: var(--clr-secondary);
}
